var cPrompt={cookie:false,init:function(){if(!this.checkCookie()){this.loadPrompt()}},checkCookie:function(){if(document.cookie.match(/cPrompt_useCookies=1/)){this.cookie=true}return this.cookie},loadPrompt:function(){var a=document.createElement("div");a.innerHTML="<div id='cookiemonster' style='margin: 15px;'>This site would like to place cookies on your computer to help make this website better. To find out more about the cookies, visit <a href='../privacy.html' style='color: #FFF;' target=\"_top\">Privacy Notice</a>.<br /><br /><input type='checkbox' id='checkbox' onclick='cPrompt.clickEvent(this);'> <label for='checkbox'>I Agree to Accept Cookies</label> <form action=\"javascript:window.location.reload()\" onsubmit=\"return validate_checkbox()\"><input type=\"submit\" value=\"Continue to Map\" \/></form></div>";a.style.cssText="background: #eee; position: absolute; width: 500px; color: #000; font-family: verdana; font-size: 11px; opacity: 1.0";a.id="cPrompt_Holder";var b=document.getElementsByTagName("body")[0];b.insertBefore(a,b.firstChild)},saveCookie:function(){document.cookie="cPrompt_useCookies=1;expires="+(new Date).toGMTString().replace(/\d{4}/,"2015")},clickEvent:function(a){if(a.checked){this.saveCookie();document.getElementById("cPrompt_Holder").style.display=""}}};if(document.addEventListener){document.addEventListener("DOMContentLoaded",function(){cPrompt.init()},false)}else if(document.attachEvent){document.attachEvent("onreadystatechange",function(){if(document.readyState==="complete"){cPrompt.init()}})}